body.script:
      // Function to handle form submission
      function handleSubmit(event) {
        event.preventDefault();

        // Get user input values
        const name = document.getElementById("name").value;
        const email = document.getElementById("email").value;
        const phone = document.getElementById("phone").value;
        const availability = document.getElementById("availability").value;
        const equipment = document.getElementById("equipment").value;

        // Create the custom message using user input values
        const customMessage = `



Preferred Schedules:

Name: ${name}

Email: ${email}

Phone: ${phone}

Preferred Schedules:

${availability}

Equipment I have:

${equipment}

          `;

        // Encode the custom message to be used in the mailto: link
        const encodedMessage = encodeURIComponent(customMessage);

        // Create the mailto: link with the encoded message
        const mailtoLink =
          "mailto:vocalizo.canto@gmail.com?subject=Singing%20Class%20-%20Preferred%20Schedules&body=" +
          encodedMessage;

        // Open the mailto: link in a new tab
        window.open(mailtoLink, "_blank");
      }

      // Add event listener to the form for form submission
      const form = document.querySelector("form");
      form.addEventListener("submit", handleSubmit);

      const navLinks = document.querySelectorAll("nav ul li a");
      const sections = document.querySelectorAll(".section");
      const burgerMenu = document.querySelector(".burger-menu");
      const navBar = document.querySelector(".nav-bar");

      navLinks.forEach((link) => {
        // Add click event listener to each navigation link
        link.addEventListener("click", (event) => {
          event.preventDefault();
          const targetId = link.getAttribute("href").substring(1);
          sections.forEach((section) => {
            section.style.display = "none";
          });
          document.getElementById(targetId).style.display = "block";
        });
      });

      burgerMenu.addEventListener("click", () => {
        navBar.classList.toggle("active");
      });
